public class HelloWorldServer {
    private final Server server;
    private final Logger logger = Logger.getLogger(HelloWorldServer.class.getName());

    public HelloWorldServer(int port) throws IOException {
        this.server = ServerBuilder.forPort(port)
                .addService(new GreeterImpl())
                .build();
    }

    public void start() throws IOException {
        server.start();
        logger.info("Server started");
        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            try {
                HelloWorldServer.this.stop();
                logger.info("Server shut down");
            } catch (InterruptedException e) {
                logger.severe("Server failed to shut down properly");
            }
        }));
    }

    public void stop() throws InterruptedException {
        if (server != null) {
            server.shutdown().awaitTermination(30, java.util.concurrent.TimeUnit.SECONDS);
        }
    }

    public void blockUntilShutdown() throws InterruptedException {
        if (server != null) {
            server.awaitTermination();
        }
    }

    public static class GreeterImpl extends StreamingGreeterGrpc.StreamingGreeterImplBase {
        @Override
        public void sayHelloStreaming(HelloRequest req, StreamObserver<HelloReply> responseObserver) {
            responseObserver.onNext(HelloReply.newBuilder().setMessage("Hello " + req.getName()).build());
            responseObserver.onNext(HelloReply.newBuilder().setMessage("Hello " + req.getName() + ", you're welcome!").build());
            responseObserver.onNext(HelloReply.newBuilder().setMessage("Hello " + req.getName() + ", I'm excited to see you!").build());
            responseObserver.onCompleted();
        }
    }

