                        }
                        for (Map.Entry<String, String> entry : headers.entrySet()) {
                            headers.put(Metadata.Key.of(entry.getKey(), Metadata.BINARY_BYTE_MARSHALLER));
                        }
                        next.interceptCall(call, headers, next);
                    }
                })
                .build();
    }

    public void start() throws IOException {
        if (running.compareAndSet(false, true)) {
            ThreadPoolExecutor executor = new ThreadPoolExecutor(
                    0, Integer.MAX_VALUE,
                    60L, TimeUnit.SECONDS,
                    new LinkedBlockingQueue<>(),
                    new ThreadFactoryBuilder().setNameFormat("TestServer-%d").build());
            server.start();
            executor.execute(() -> {
                try {
                    server.awaitTermination(Long.MAX_VALUE, TimeUnit.NANOSECONDS);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            });
        }
    }

    public void stop() {
        if (running.compareAndSet(true, false)) {
            server.shutdown();
            try {
                if (!server.awaitTermination(5, TimeUnit.SECONDS)) {
                    server.shutdownNow();
                }
            } catch (InterruptedException e) {
                server.shutdownNow();
            }
        }
    }

    public int port() {
        return server.getPort();
    }

    public TraceContextOrSamplingFlags takeRequest() throws InterruptedException {
        return requests.take();
    }

    public void enqueueDelay(long millis) {
        delayQueue.offer(millis);
    }

    private static class TestServiceImpl extends TestServiceGrpc.TestServiceImplBase {
        @Override
        public void test(TestRequest request, StreamObserver<TestResponse> responseObserver) {
            try {
                long delay = delayQueue.poll();
                if (delay != null) {
                    Thread.sleep(delay);
                }
                TraceContextOrSamplingFlags context = requests.take();
                responseObserver.onNext(TestResponse.newBuilder().setMessage("Processed").build());
                responseObserver.onCompleted();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                responseObserver.onError(e);
            }
        }
    }
}
