package com.example.grpc;

import io.grpc.Server;
import io.grpc.ServerBuilder;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.ApplicationContext;
import org.springframework.context.ApplicationListener;
import org.springframework.context.event.ContextClosedEvent;
import org.springframework.context.event.ContextRefreshedEvent;
import org.springframework.stereotype.Component;

import java.util.Map;

@Component
public class GrpcLauncher implements ApplicationListener<ContextRefreshedEvent>, ApplicationListener<ContextClosedEvent> {

    private Server server;
    @Value("${grpc.server.port}")
    private int grpcServerPort;
    private static final int DEFAULT_MAX_MESSAGE_SIZE = 10 * 1024 * 1024;

    public void grpcStart(Map<String, Object> grpcServiceBeanMap) {
        server = ServerBuilder.forPort(grpcServerPort)
                .maxInboundMessageSize(DEFAULT_MAX_MESSAGE_SIZE)
                .addService(new GrpcServerInterceptor())
                .build();
        server.start();
        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            grpcStop();
            System.out.println("Shutting down gRPC server.");
        }));
    }

    public void grpcStop() {
        if (server != null) {
            server.shutdown();
        }
    }

    @Override
    public void onApplicationEvent(ContextRefreshedEvent event) {
        ApplicationContext applicationContext = event.getApplicationContext();
        Map<String, Object> grpcServiceBeanMap = applicationContext.getBeansWithAnnotation(GrpcService.class);
        grpcStart(grpcServiceBeanMap);
    }

    @Override
    public void onApplicationEvent(ContextClosedEvent event) {
        grpcStop();
    }
}
