                @Override
                public void onCompleted() {
                    Data.DataResponse res = Data.DataResponse.newBuilder()
                            .setValue(items.toString())
                            .build();

                    resObserver.onNext(res);
                    resObserver.onCompleted();
                }
            };
        }
    }

