import io.grpc.Server;
import io.grpc.ServerBuilder;
import io.grpc.stub.StreamObserver;
import io.prometheus.client.Counter;
import io.prometheus.client.exporter.HTTPServer;
import io.prometheus.client.hotspot.DefaultExports;
import java.io.IOException;

public class DataHandler {
    private static final Logger LOG = LoggerFactory.getLogger(DataHandler.class);
    private static final int PORT = 1111;
    private final Counter syncRequests = Counter.build().name("sync_requests").help("Synchronous requests").register();
    private final Counter streamingRequests = Counter.build().name("streaming_requests").help("Streaming requests").register();
    private final Server grpcServer;
    private final HTTPServer prometheusHttpServer;

    public DataHandler() throws IOException {
        this.grpcServer = ServerBuilder.forPort(PORT)
                .addService(new DataImpl())
                .intercept(new PrometheusServerInterceptor(syncRequests, streamingRequests))
                .build();
        this.prometheusHttpServer = new HTTPServer(1234);
        DefaultExports.initialize();
    }

    public void start() throws IOException {
        LOG.info("Starting gRPC server on port {}", PORT);
        grpcServer.start();
    }

    public void blockUntilShutdown() throws InterruptedException {
        LOG.info("Server shut down");
        grpcServer.awaitTermination();
    }

    public void stop() {
        LOG.info("Shutting down gRPC server");
        if (grpcServer != null) {
            grpcServer.shutdown();
        }
        if (prometheusHttpServer != null) {
            prometheusHttpServer.stop(0);
        }
    }

    public static class DataImpl extends DataServiceGrpc.DataServiceImplBase {
        @Override
        public void get(DataRequest request, StreamObserver<DataResponse> responseObserver) {
            syncRequests.inc();
            String uppercaseData = request.getData().toUpperCase();
            responseObserver.onNext(DataResponse.newBuilder().setData(uppercaseData).build());
            responseObserver.onCompleted();
        }

        @Override
        public StreamObserver<EmptyRequest> streamingGet(StreamObserver<DataResponse> responseObserver) {
            streamingRequests.inc();
            for (int i = 0; i < 10; i++) {
                responseObserver.onNext(DataResponse.newBuilder().setData("Response " + (i + 1)).build());
            }
            responseObserver.onCompleted();
            return null;
        }

        @Override
        public StreamObserver<DataRequest> streamingPut(StreamObserver<DataResponse> responseObserver) {
            return new StreamObserver<DataRequest>() {
                private StringBuilder dataBuilder = new StringBuilder();

                @Override
                public void onNext(DataRequest value) {
                    dataBuilder.append(value.getData());
                }

                @Override
                public void onError(Throwable t) {
                    responseObserver.onError(t);
                }

                @Override
                public void onCompleted() {
                    String modifiedData = dataBuilder.toString().toLowerCase();
                    responseObserver.onNext(DataResponse.newBuilder().setData(modifiedData).build());
                    responseObserver.onCompleted();
                }
            };
        }
    }
}
