public class EchoServer extends Thread {
    private int port;
    private ServerSocket serverSocket;
    private List<Thread> threads = Collections.synchronizedList(new ArrayList<>());
    private List<Socket> sockets = Collections.synchronizedList(new ArrayList<>());
    private List<Exception> exceptions = Collections.synchronizedList(new ArrayList<>());
    private AtomicBoolean renegotiate = new AtomicBoolean(false);

    public EchoServer(int port) {
        this.port = port;
        try {
            serverSocket = new ServerSocket(port);
        } catch (IOException e) {
            exceptions.add(e);
        }
    }

    public EchoServer(SSLFactory sslFactory, int port) {
        this.port = port;
        try {
            SSLServerSocketFactory sslServerSocketFactory = sslFactory.getServerSocketFactory();
            serverSocket = sslServerSocketFactory.createServerSocket(port);
        } catch (IOException | GeneralSecurityException e) {
            exceptions.add(e);
        }
    }

    public void renegotiate() {
        renegotiate.set(true);
    }

    public void run() {
        try {
            while (!Thread.interrupted()) {
                Socket clientSocket = serverSocket.accept();
                threads.add(new Thread(new EchoHandler(clientSocket)));
                sockets.add(clientSocket);
                threads.get(threads.size() - 1).start();
            }
        } catch (IOException e) {
            exceptions.add(e);
        }
    }

    public void closeConnections() {
        for (Socket socket : sockets) {
            try {
                socket.close();
            } catch (IOException e) {
                exceptions.add(e);
            }
        }
        sockets.clear();
    }

    public void close() {
        closeConnections();
        for (Thread thread : threads) {
            thread.interrupt();
        }
        try {
            serverSocket.close();
        } catch (IOException e) {
            exceptions.add(e);
        }
    }

    public int getExceptionCount() {
        return exceptions.size();
    }

    private class EchoHandler implements Runnable {
        private Socket clientSocket;

        public EchoHandler(Socket clientSocket) {
            this.clientSocket = clientSocket;
        }

        public void run() {
            try {
                BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                PrintWriter out = new PrintWriter(clientSocket.getOutputStream(), true);
                String inputLine;
                while ((inputLine = in.readLine()) != null) {
                    out.println(inputLine);
                    if (renegotiate.get()) {
                        renegotiate.set(false);
                        SSLSession session = ((SSLSocket) clientSocket).getSession();
                        session.getPeerCertificates();
                    }
                }
            } catch (IOException e) {
                exceptions.add(e);
            } finally {
                try {
                    clientSocket.close();
                } catch (IOException e) {
                    exceptions.add(e);
                }
            }
        }
    }
}
