import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.InetAddress;
import java.net.ServerSocket;
import java.net.Socket;

public class DNSConnection {
    public static final int PORT = 53;
    public static final int MAX_MSG_LEN = 65535;
    private ServerSocket listener;
    private Socket socket;
    private byte[] msgBytes;
    private int msgLen;
    private InputStream in;
    private OutputStream out;
    private byte[] lenBuf;

    public void listen() throws IOException {
        listener = new ServerSocket(PORT);
    }

    public void stopListening() throws IOException {
        if (listener != null) {
            listener.close();
        }
    }

    public void openIncoming() throws IOException {
        socket = listener.accept();
        in = socket.getInputStream();
        out = socket.getOutputStream();
        lenBuf = new byte[2];
    }

    public void open(InetAddress server) throws IOException {
        socket = new Socket(server, PORT);
        in = socket.getInputStream();
        out = socket.getOutputStream();
        lenBuf = new byte[2];
    }

    public void send(byte[] msgBytes) throws IOException {
        out.write(intToBytes(msgBytes.length));
        out.write(msgBytes);
    }

    public byte[] receive(boolean wait) throws IOException {
        if (wait) {
            in.read(lenBuf);
            msgLen = bytesToInt(lenBuf);
            if (msgLen > MAX_MSG_LEN) {
                throw new IOException("Message length exceeds maximum allowed");
            }
            msgBytes = new byte[msgLen];
            in.read(msgBytes);
        } else {
            int available = in.available();
            if (available > 0) {
                in.read(lenBuf);
                msgLen = bytesToInt(lenBuf);
                if (msgLen > MAX_MSG_LEN) {
                    throw new IOException("Message length exceeds maximum allowed");
                }
                msgBytes = new byte[msgLen];
                in.read(msgBytes);
            } else {
                msgBytes = new byte[0];
            }
        }
        return msgBytes;
    }

    public void close() throws IOException {
        if (socket != null) {
            socket.close();
        }
        if (in != null) {
            in.close();
        }
        if (out != null) {
            out.close();
        }
    }

    private byte[] intToBytes(int value) {
        return new byte[]{
                (byte) (value >>> 24),
                (byte) (value >>> 16),
                (byte) (value >>> 8),
                (byte) value
        };
    }

    private int bytesToInt(byte[] bytes) {
        return (bytes[0] & 0xFF) << 24 |
                (bytes[1] & 0xFF) << 16 |
                (bytes[2] & 0xFF) << 8 |
                (bytes[3] & 0xFF);
    }
}
