public class HttpUrlFetcher {
    public InputStream fetchUrlContent(String urlStr) {
        if (!urlStr.startsWith("http://") && !urlStr.startsWith("https://")) {
            return null;
        }

        HttpURLConnection connection = null;
        try {
            URL url = new URL(urlStr);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("GET");
            connection.setInstanceFollowRedirects(false);
            connection.setConnectTimeout(5000);
            connection.setReadTimeout(5000);

            Map<String, String> cookies = new HashMap<>();
            String cookieHeader = connection.getHeaderField("Set-Cookie");
            if (cookieHeader != null) {
                String[] cookiePairs = cookieHeader.split(";");
                for (String pair : cookiePairs) {
                    String[] keyValue = pair.split("=");
                    if (keyValue.length == 2) {
                        cookies.put(keyValue[0].trim(), keyValue[1].trim());
                    }
                }
            }

            int redirectCount = 0;
            while (connection.getResponseCode() / 100 == 3 && redirectCount < 10) {
                String location = connection.getHeaderField("Location");
                if (location == null) {
                    break;
                }
                connection.disconnect();
                connection = (HttpURLConnection) new URL(location).openConnection();
                connection.setRequestMethod("GET");
                connection.setInstanceFollowRedirects(false);
                connection.setConnectTimeout(5000);
                connection.setReadTimeout(5000);

                for (Map.Entry<String, String> cookieEntry : cookies.entrySet()) {
                    connection.setRequestProperty("Cookie", cookieEntry.getKey() + "=" + cookieEntry.getValue());
                }

                redirectCount++;
            }

            if (connection.getResponseCode() / 100 != 3) {
                return connection.getInputStream();
            }
        } catch (Exception e) {
            return null;
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }

        return null;
    }
}
