import java.util.concurrent.Executors;

public class WebSocketServer {
    private ServerSocket serverSocket;
    private int port;
    private Thread serverThread;
    private ExecutorService executorService;

    public WebSocketServer(int port) {
        this.port = port;
        this.executorService = Executors.newCachedThreadPool();
    }

    public void start() {
        serverThread = new Thread(() -> {
            try {
                serverSocket = new ServerSocket(port);
                while (!Thread.currentThread().isInterrupted()) {
                    Socket client = serverSocket.accept();
                    executorService.submit(() -> handleClientConnection(client));
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                stop();
            }
        });
        serverThread.start();
    }

    public void stop() {
        if (serverSocket != null) {
            try {
                serverSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (serverThread != null) {
            serverThread.interrupt();
        }
        if (executorService != null) {
            executorService.shutdown();
        }
    }

    private void handleClientConnection(Socket client) {
        try {
            BufferedReader in = new BufferedReader(new InputStreamReader(client.getInputStream()));
            PrintWriter out = new PrintWriter(client.getOutputStream(), true);

            String request = in.readLine();
            if (request != null && request.startsWith("GET")) {
                String key = request.substring(request.indexOf("Sec-WebSocket-Key: ") + 17, request.indexOf("\r\n"));
                String accept = generateFinalKey(key);
                out.println("HTTP/1.1 101 Switching Protocols");
                out.println("Upgrade: websocket");
                out.println("Connection: Upgrade");
                out.println("Sec-WebSocket-Accept: " + accept);
                out.println();
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                client.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private String generateFinalKey(String in) {
        String hash = in + "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        MessageDigest md = MessageDigest.getInstance("SHA-1");
        md.update(hash.getBytes(StandardCharsets.UTF_8));
        byte[] digest = md.digest();
        StringBuilder hexString = new StringBuilder();
        for (byte b : digest) {
            String hex = Integer.toHexString(0xff & b);
            if (hex.length() == 1) hexString.append('0');
            hexString.append(hex);
        }
        return hexString.toString();
    }
}
