import java.io.*;
import java.net.*;
import java.nio.*;
import java.nio.channels.*;
import java.util.*;
import java.util.concurrent.*;

public class WebSocketServer {
    private ServerSocket serverSocket;
    private int port;

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        while (true) {
            new Thread(() -> handleConnection()).start();
        }
    }

    private void handleConnection() throws IOException {
        Socket clientSocket = serverSocket.accept();
        try {
            InputStream input = clientSocket.getInputStream();
            OutputStream output = clientSocket.getOutputStream();

            BufferedReader reader = new BufferedReader(new InputStreamReader(input));
            BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(output));

            String requestLine = reader.readLine();
            if (requestLine == null) {
                return;
            }

            String[] tokens = requestLine.split(" ");
            if (tokens[0].equals("GET") && tokens[1].startsWith("/")) {
                String[] headers = reader.lines().toArray(String[]::new);
                if (performHandshake(headers, writer)) {
                    sendBinaryFrame(output);
                }
            }
        } finally {
            clientSocket.close();
        }
    }

    private boolean performHandshake(String[] headers, BufferedWriter writer) throws IOException {
        String key = findHeader(headers, "Sec-WebSocket-Key");
        String responseKey = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        String response = "HTTP/1.1 101 Switching Protocols\r\n" +
                "Upgrade: websocket\r\n" +
                "Connection: Upgrade\r\n" +
                "Sec-WebSocket-Accept: " + getAcceptKey(key, responseKey) + "\r\n\r\n";
        writer.write(response);
        writer.flush();
        return true;
    }

    private String findHeader(String[] headers, String headerName) {
        for (String header : headers) {
            if (header.startsWith(headerName)) {
                return header.substring(headerName.length() + 1).trim();
            }
        }
        return null;
    }

    private String getAcceptKey(String key, String responseKey) {
        return Base64.getEncoder().encodeToString((key + responseKey).getBytes());
    }

    private void sendBinaryFrame(OutputStream output) throws IOException {
        byte[] data = new byte[1024];
        new Random().nextBytes(data);
        ByteBuffer buffer = ByteBuffer.allocate(2 + data.length);
        buffer.put((byte) 0x82); // Fin + Op Code
        buffer.putShort((short) data.length);
        buffer.put(data);
        output.write(buffer.array());
        output.flush();
    }

    public void stop() throws IOException {
        if (serverSocket != null) {
            serverSocket.close();
        }
    }
}
