import java.io.*;
import java.net.*;

public class ConnectionManipulationProxyServer {
    private int localPort;
    private String host;
    private int remotePort;
    private Thread proxyThread;
    private Object enableLock;
    private boolean enableProxy;
    private boolean running;
    private Socket client;
    private Socket server;
    private ServerSocket serverSocket;

    public ConnectionManipulationProxyServer(String host, int remotePort, int localPort) {
        this.host = host;
        this.remotePort = remotePort;
        this.localPort = localPort;
        this.enableLock = new Object();
        this.enableProxy = false;
        this.running = false;
    }

    public void startProxy() {
        synchronized (enableLock) {
            if (!enableProxy) {
                proxyThread = new Thread(this::run);
                proxyThread.start();
                enableProxy = true;
            }
        }
    }

    public void stopProxy() {
        synchronized (enableLock) {
            if (enableProxy) {
                running = false;
                try {
                    proxyThread.join();
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
                enableProxy = false;
            }
        }
    }

    public void killOpenSockets() {
        if (client != null) {
            try {
                client.close();
            } catch (IOException e) {
                // Log and ignore
            }
        }
        if (server != null) {
            try {
                server.close();
            } catch (IOException e) {
                // Log and ignore
            }
        }
        if (serverSocket != null) {
            try {
                serverSocket.close();
            } catch (IOException e) {
                // Log and ignore
            }
        }
    }

    public void run() {
        try {
            serverSocket = new ServerSocket(localPort);
            running = true;
            while (running) {
                client = serverSocket.accept();
                server = new Socket(host, remotePort);
                new Thread(new ProxyHandler(client, server)).start();
            }
        } catch (IOException e) {
            // Log and handle exception
        } finally {
            killOpenSockets();
        }
    }

    public int getLocalPort() {
        return localPort;
    }

    private class ProxyHandler implements Runnable {
        private Socket clientSocket;
        private Socket serverSocket;

        public ProxyHandler(Socket clientSocket, Socket serverSocket) {
            this.clientSocket = clientSocket;
            this.serverSocket = serverSocket;
        }

        @Override
        public void run() {
            try {
                InputStream clientIn = clientSocket.getInputStream();
                OutputStream clientOut = clientSocket.getOutputStream();
                InputStream serverIn = serverSocket.getInputStream();
                OutputStream serverOut = serverSocket.getOutputStream();

                byte[] buffer = new byte[1024];
                int bytesRead;
                while ((bytesRead = clientIn.read(buffer)) != -1) {
                    serverOut.write(buffer, 0, bytesRead);
                }
                while ((bytesRead = serverIn.read(buffer)) != -1) {
                    clientOut.write(buffer, 0, bytesRead);
                }
            } catch (IOException e) {
                // Log and handle exception
            } finally {
                try {
                    clientSocket.close();
                    serverSocket.close();
                } catch (IOException e) {
                    // Log and ignore
                }
            }
        }
    }
