                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

    public void run() {
        try {
            serverSocket = new ServerSocket(localPort);
            running = true;
            while (running) {
                client = serverSocket.accept();
                server = new Socket(host, remotePort);
                new Thread(() -> {
                    try {
                        forwardTraffic(client, server);
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            killOpenSockets();
        }
    }

    public int getLocalPort() {
        return localPort;
    }

    private void forwardTraffic(Socket clientSocket, Socket serverSocket) throws IOException {
        BufferedReader clientReader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
        BufferedWriter clientWriter = new BufferedWriter(new OutputStreamWriter(clientSocket.getOutputStream()));
        BufferedReader serverReader = new BufferedReader(new InputStreamReader(serverSocket.getInputStream()));
        BufferedWriter serverWriter = new BufferedWriter(new OutputStreamWriter(serverSocket.getOutputStream()));

        Thread clientToServer = new Thread(() -> {
            try {
                String line;
                while ((line = clientReader.readLine()) != null) {
                    serverWriter.write(line);
                    serverWriter.newLine();
                    serverWriter.flush();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        });

        Thread serverToClient = new Thread(() -> {
            try {
                String line;
                while ((line = serverReader.readLine()) != null) {
                    clientWriter.write(line);
                    clientWriter.newLine();
                    clientWriter.flush();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        });

        clientToServer.start();
        serverToClient.start();

        try {
            clientToServer.join();
            serverToClient.join();
        } catch (InterruptedException e) {
            e.printStackTrace();
        }

        clientSocket.close();
        serverSocket.close();
    }
