import java.io.*;
import java.net.*;

public class EchoBioServer {
    private static final byte[] FIRST_BYTES = "Welcome to the Echo Server!".getBytes();
    private ServerSocket serverSocket;

    public EchoBioServer(int port) {
        try {
            serverSocket = new ServerSocket(port);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void run() {
        try {
            while (true) {
                Socket clientSocket = serverSocket.accept();
                new BioConnection(clientSocket).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private class BioConnection extends Thread {
        private Socket socket;
        private InputStream input;
        private OutputStream output;
        private byte[] readBuffer = new byte[1024];
        private byte[] writeBuffer = new byte[1024];

        public BioConnection(Socket socket) {
            this.socket = socket;
            try {
                input = socket.getInputStream();
                output = socket.getOutputStream();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

        public void run() {
            try {
                output.write(FIRST_BYTES);
                output.flush();
                int bytesRead;
                while ((bytesRead = input.read(readBuffer)) != -1) {
                    writeBuffer = new byte[bytesRead];
                    System.arraycopy(readBuffer, 0, writeBuffer, 0, bytesRead);
                    output.write(writeBuffer);
                    output.flush();
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                try {
                    socket.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

    public static void main(String[] args) {
        EchoBioServer server = new EchoBioServer(1234);
        server.run();
    }
}
