class TcpServer extends Thread {
    private IAppFrame frame;
    private int port;
    private String file;

    public TcpServer(int port, IAppFrame frame) {
        this.port = port;
        this.frame = frame;
    }

    public TcpServer(int port, IAppFrame frame, String file) {
        this.port = port;
        this.frame = frame;
        this.file = file;
    }

    public static boolean checkExistInstance(int port) {
        try {
            Socket socket = new Socket("localhost", port);
            socket.close();
            return true;
        } catch (IOException e) {
            return false;
        }
    }

    private static boolean ask(String host, String cmd, int port) {
        try {
            Socket socket = new Socket(host, port);
            PrintWriter out = new PrintWriter(socket.getOutputStream(), true);
            out.println(cmd);
            BufferedReader in = new BufferedReader(new InputStreamReader(socket.getInputStream()));
            String response = in.readLine();
            socket.close();
            return "OK".equals(response);
        } catch (IOException e) {
            return false;
        }
    }

    @Override
    public void run() {
        try (ServerSocket serverSocket = new ServerSocket(port)) {
            while (true) {
                Socket clientSocket = serverSocket.accept();
                BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                PrintWriter out = new PrintWriter(clientSocket.getOutputStream(), true);
                String command = in.readLine();
                if ("GET_WINDOW_TITLES".equals(command)) {
                    out.println(frame.getWindowTitles());
                } else if ("ACTIVATE_WINDOW".equals(command)) {
                    frame.activateWindow();
                } else if ("OPEN_FILE".equals(command)) {
                    frame.openFile(file);
                }
                out.println("OK");
                in.close();
                out.close();
                clientSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

interface IAppFrame {
    String getWindowTitles();
    void activateWindow();
    void openFile(String file);
}
