        }
        String description = path.substring(descriptionIndex + 1);
        Map<String, String> queries = new HashMap<>();
        int queryIndex = description.indexOf("?");
        if (queryIndex == -1) {
            queries.put(ISchemaAction.DESCRIPTION, description);
