public class EmbedHttpServer {
    private int port;
    private ServerSocket serverSocket;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        new Thread(this::run).start();
    }

    public void stop() throws IOException {
        serverSocket.close();
    }

    public void run() {
        try {
            while (!serverSocket.isClosed()) {
                Socket clientSocket = serverSocket.accept();
                new Thread(() -> {
                    try {
                        handleRequest(clientSocket);
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    protected void handle(String method, String path, Map<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response) throws IOException {
        // To be overridden by subclasses
    }

    private void handleRequest(Socket clientSocket) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
        OutputStream out = clientSocket.getOutputStream();
        String requestLine = in.readLine();
        if (requestLine == null) {
            return;
        }

        String[] requestParts = requestLine.split(" ");
        String method = requestParts[0];
        String path = requestParts[1];

        Map<String, String> headers = new HashMap<>();
        String headerLine;
        while (!(headerLine = in.readLine()).isEmpty()) {
            int colonIndex = headerLine.indexOf(':');
            if (colonIndex > 0) {
                headers.put(headerLine.substring(0, colonIndex).trim(), headerLine.substring(colonIndex + 1).trim());
            }
        }

        Map<String, String> queries = parsePath(path);

        byte[] content = new byte[1024];
        int bytesRead;
        StringBuilder contentBuilder = new StringBuilder();
        while ((bytesRead = in.read(content)) != -1) {
            contentBuilder.append(new String(content, 0, bytesRead));
        }

        handle(method, path, headers, queries, new ByteArrayInputStream(contentBuilder.toString().getBytes()), out);
        out.close();
        in.close();
        clientSocket.close();
    }

    protected Map<String, String> parsePath(String path) {
        Map<String, String> queries = new HashMap<>();
        if (path.contains("?")) {
            String queryPart = path.substring(path.indexOf('?') + 1);
            String[] queryPairs = queryPart.split("&");
            for (String pair : queryPairs) {
                int index = pair.indexOf('=');
                if (index > 0) {
                    queries.put(pair.substring(0, index), pair.substring(index + 1));
                }
            }
        }
        return queries;
    }
}
