            while (true) {
                Socket clnt = socket.accept();
                new Thread(new Service(clnt)).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void process(Socket clnt) throws IOException {
        InputStream in = clnt.getInputStream();
        OutputStream out = clnt.getOutputStream();
        String cmd = readLine(in);
        doReply(in, out, cmd);
        clnt.close();
    }

    private String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int i;
        byte[] buffer = new byte[256];
        while ((i = in.read(buffer)) != -1) {
            for (int j = 0; j < i; j++) {
                if (buffer[j] == '\n') {
                    return sb.toString();
                }
                sb.append((char) buffer[j]);
            }
        }
        return null;
    }

    private void skipLine(InputStream in) throws IOException {
        int i;
        byte[] buffer = new byte[256];
        while ((i = in.read(buffer)) != -1) {
            if (buffer[i - 1] == '\n') {
                break;
            }
        }
    }

    private void doReply(InputStream in, OutputStream out, String cmd) throws IOException {
        String[] tokens = cmd.split(" ");
        if (tokens[0].equals("GET")) {
            sendHeader(out, 0, 0);
            if (tokens.length > 1) {
                letUsersSendClassfile(out, tokens[1], 0);
            } else {
                sendHeader(out, 0, 0);
            }
        } else {
            replyError(out, new BadHttpRequest("Invalid command"));
        }
    }

    private boolean letUsersSendClassfile(OutputStream out, String filename, int length) throws IOException {
        try {
            Class<?> c = classPool.makeClass(filename);
            byte[] bytecode = c.toBytecode();
            sendHeader(out, bytecode.length, 1);
            out.write(bytecode);
            out.flush();
            return true;
        } catch (Exception e) {
            replyError(out, new BadHttpRequest("Class not found"));
            return false;
        }
    }

    private void sendHeader(OutputStream out, long dataLength, int filetype) throws IOException {
        out.write("HTTP/1.0 200 OK".getBytes(endofline));
