        try {
            InputStream in = clnt.getInputStream();
            OutputStream out = clnt.getOutputStream();
            out.flush();
            String cmd = readLine(in);
            doReply(in, out, cmd);
            clnt.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int i;
        do {
            i = in.read();
            if (i == -1) return null;
            sb.append((char) i);
        } while (i != '\n' && i != '\r');
        return sb.toString();
    }

    public void skipLine(InputStream in) throws IOException {
