import java.io.*;
import java.net.*;
import java.util.*;

public class EmbedHttpServer {
    private int port;
    private ServerSocket serverSocket;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        new Thread(this::run).start();
    }

    public void stop() throws IOException {
        serverSocket.close();
    }

    public abstract void handle(String method, String path, HashMap<String, String> headers, InputStream input, OutputStream response);

    private void run() {
        try {
            while (!Thread.currentThread().isInterrupted()) {
                Socket clientSocket = serverSocket.accept();
                new Thread(() -> {
                    try {
                        handleRequest(clientSocket);
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void handleRequest(Socket clientSocket) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
        OutputStream out = clientSocket.getOutputStream();
        String requestLine = in.readLine();
        if (requestLine == null) {
            return;
        }
        String[] requestParts = requestLine.split(" ");
        String method = requestParts[0];
        String path = requestParts[1];
        HashMap<String, String> headers = new HashMap<>();
        String line;
        while (!(line = in.readLine()).isEmpty()) {
            int colonIndex = line.indexOf(':');
            if (colonIndex > 0) {
                headers.put(line.substring(0, colonIndex).trim(), line.substring(colonIndex + 1).trim());
            }
        }
        byte[] bodyBytes = new byte[4096];
        int bytesRead;
        StringBuilder bodyBuilder = new StringBuilder();
        while ((bytesRead = in.read(bodyBytes)) != -1) {
            bodyBuilder.append(new String(bodyBytes, 0, bytesRead));
        }
        String body = bodyBuilder.toString();
        handle(method, path, headers, new ByteArrayInputStream(body.getBytes()), out);
        clientSocket.close();
    }

    public static class BodyInputStream extends InputStream {
        private InputStream ins;
        private int n;

        public BodyInputStream(InputStream ins, int n) {
            this.ins = ins;
            this.n = n;
        }

        @Override
        public int available() throws IOException {
            return n;
        }

        @Override
        public int read() throws IOException {
            if (n <= 0) {
                return -1;
            }
            n--;
            return ins.read();
        }

        @Override
        public int read(byte[] b, int off, int len) throws IOException {
            if (n <= 0) {
                return -1;
            }
            int toRead = Math.min(len, n);
            int bytesRead = ins.read(b, off, toRead);
            n -= bytesRead;
            return bytesRead;
        }

        @Override
        public long skip(long n) throws IOException {
            throw new IOException("Skipping is not supported");
        }

        @Override
        public void close() throws IOException {
            ins.close();
        }
    }
}
