import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.net.DatagramSocket;
import java.net.Socket;
import java.util.Map;
import java.util.HashMap;
import java.util.logging.Logger;

public class NetworkForwarder {
    private static final Logger LOGGER = Logger.getLogger(NetworkForwarder.class.getName());
    private InetAddress destination;
    private DatagramSocket connectionUdp;
    private Map<InetSocketAddress, Socket> connectionsTcp;

    public NetworkForwarder(Config config) {
        destination = config.getDestination();
        connectionUdp = new DatagramSocket();
    }

    public void forward(InetSocketAddress source, int port, boolean datagram, byte[] data) {
        if (datagram) {
            connectionUdp.send(new DatagramPacket(data, data.length, destination, port));
        } else {
            Socket socket = connectionsTcp.get(source);
            if (socket == null) {
                try {
                    socket = new Socket(destination, port);
                    connectionsTcp.put(source, socket);
                } catch (Exception e) {
                    LOGGER.warning("Failed to create TCP connection: " + e.getMessage());
                    return;
                }
            }
            try {
                socket.getOutputStream().write(data);
            } catch (Exception e) {
                LOGGER.warning("Failed to forward data: " + e.getMessage());
                disconnect(source);
            }
        }
    }

    public void disconnect(InetSocketAddress source) {
        Socket socket = connectionsTcp.get(source);
        if (socket != null) {
            try {
                socket.close();
                connectionsTcp.remove(source);
            } catch (Exception e) {
                LOGGER.warning("Failed to disconnect: " + e.getMessage());
            }
        }
    }
}
