    private BufferedReader reader;

    public MocoSocketHelper(String target, int port) {
        socket = new Socket();
        address = new SocketAddress(target, port);
    }

    public void connect() throws IOException {
        socket.connect(address);
        os = new PrintStream(socket.getOutputStream());
        reader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
    }

    public String send(String request) throws IOException {
        os.println(request);
        os.flush();
        return reader.readLine();
    }

    public String send(String request, int readCount) throws IOException {
        os.println(request);
        os.flush();
        StringBuilder response = new StringBuilder();
        char[] buffer = new char[readCount];
        int read;
        while ((read = reader.read(buffer)) != -1) {
            response.append(buffer, 0, read);
            if (response.length() >= readCount) {
                break;
            }
        }
        return response.toString();
    }

    @Override
    public void close() throws IOException {
        if (reader != null) {
            reader.close();
        }
        if (os != null) {
