          } finally {
            if (entered) {
              BTraceRuntime.leave();
            }
          }
        });
  }

  public static Statsd getInstance() {
    return Singleton.INSTANCE;
  }

  private static final class Singleton {
    private static final Statsd INSTANCE = new Statsd();
  }
