    public void start() {
        super.start();
        try {
            OutputStream outputStream = datagramSocketOutputStream(host, port);
            setOutputStream(outputStream);
        } catch (Exception e) {
            throw new RuntimeException("Error starting DropwizardUdpSocketAppender", e);
        }
    }

