        DatagramSocket socket = new DatagramSocket();
        socket.connect(InetAddress.getByName(host), port);
        return new OutputStream() {
            @Override
            public void write(int b) {
                throw new UnsupportedOperationException("UDP does not support byte-level writes");
            }

            @Override
            public void write(byte[] b, int off, int len) {
                DatagramPacket packet = new DatagramPacket(b, len, InetAddress.getByName(host), port);
                socket.send(packet);
            }

            @Override
            public void close() {
                socket.close();
            }
