public class DnsUdpClient {
    public static InetSocketAddress[] DNS_SERVERS;

    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        socket.connect(dnsServer);
        DatagramPacket sendPacket = new DatagramPacket(query, query.length, dnsServer);
        socket.send(sendPacket);
        byte[] receiveData = new byte[1024];
        DatagramPacket receivePacket = new DatagramPacket(receiveData, receiveData.length);
        socket.receive(receivePacket);
        socket.close();
        return toIps(receivePacket.getData());
    }

    public List<Inet4Address> toIps(byte[] buffer) {
        List<Inet4Address> ips = new ArrayList<>();
        try {
            DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
            packet.setData(buffer);
            packet.setLength(buffer.length);
            InetAddress address = packet.getAddress();
            ips.add((Inet4Address) address);
        } catch (Exception e) {
            e.printStackTrace();
        }
        return ips;
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        socket.connect(dnsServer);
        DatagramPacket sendPacket = new DatagramPacket(query, query.length, dnsServer);
        socket.send(sendPacket);
        byte[] receiveData = new byte[1024];
        DatagramPacket receivePacket = new DatagramPacket(receiveData, receiveData.length);
        socket.receive(receivePacket);
        socket.close();
        return toTXT(receivePacket.getData());
    }

    public String toTXT(byte[] buffer) {
        String txt = "";
        try {
            String response = new String(buffer);
            txt = response.split("\n")[0];
        } catch (RuntimeException e) {
            throw new RuntimeException("No TXT record found", e);
        }
        return txt;
    }
}
