            os.write(encodeFormData(form).getBytes());
            os.flush();
            os.close();

            return readResponse(connection);
        } catch (Exception e) {
            logger.severe(e.getMessage());
            return null;
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private static String readResponse(HttpURLConnection connection) throws Exception {
        StringBuilder response = new StringBuilder();
        try (BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()))) {
            String inputLine;
            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }
        }
        return response.toString();
    }

    private static String encodeFormData(Map<String, String> formData) throws Exception {
        StringBuilder formDataBuilder = new StringBuilder();
        for (Map.Entry<String, String> entry : formData.entrySet()) {
            if (formDataBuilder.length() > 0) {
                formDataBuilder.append("&");
            }
            formDataBuilder.append(entry.getKey()).append("=").append(entry.getValue());
        }
        return formDataBuilder.toString();
    }
}
