import java.io.BufferedReader;
import java.io.DataOutputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.security.cert.X509Certificate;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.logging.Level;
import java.util.logging.Logger;

public class SimpleHttpUtils {
    private static final Logger logger = Logger.getLogger(SimpleHttpUtils.class.getName());
    private static final String DEFAULT_CHARSET = "utf-8";
    private static final String HTTP_METHOD_POST = "POST";
    private static final String HTTP_METHOD_GET = "GET";
    private static final String HTTP_ERROR_MESSAGE = "HTTP Error";
    private static final int DEFAULT_READ_TIMEOUT = 10000;
    private static final int DEFAULT_CONNECT_TIMEOUT = 10000;
    private static final String HTTP_PREFIX = "http://";
    private static final String HTTPS_PREFIX = "https://";
    private static final int MAX_FETCHSIZE = 1024 * 1024 * 10;
    private static TrustManager[] trustAnyManagers = new TrustManager[]{new TrustAnyTrustManager()};

    public static String httpPost(String url, Map<String, String> params) throws IOException {
        return httpRequest(url, params, HTTP_METHOD_POST, DEFAULT_CHARSET, new HashMap<>());
    }

    public static String httpGet(String url, Map<String, String> params) throws IOException {
        return httpRequest(url, params, HTTP_METHOD_GET, DEFAULT_CHARSET, new HashMap<>());
    }

    public static String httpRequest(String url, Map<String, String> params, String method, String charSet, Map<String, String> headers) throws IOException {
        URL obj = new URL(url);
        HttpURLConnection con = (HttpURLConnection) obj.openConnection();
        con.setRequestMethod(method);
        con.setConnectTimeout(DEFAULT_CONNECT_TIMEOUT);
        con.setReadTimeout(DEFAULT_READ_TIMEOUT);
        con.setDoOutput(true);

        if (headers != null) {
            for (Map.Entry<String, String> entry : headers.entrySet()) {
                con.setRequestProperty(entry.getKey(), entry.getValue());
            }
        }

        if (params != null) {
            String query = mapToQueryString(params, charSet);
            try (DataOutputStream wr = new DataOutputStream(con.getOutputStream())) {
                wr.writeBytes(query);
                wr.flush();
            }
        }

        StringBuilder response = new StringBuilder();
        try (BufferedReader in = new BufferedReader(new InputStreamReader(con.getInputStream(), charSet))) {
            String inputLine;
            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }
        }

        con.disconnect();
        return response.toString();
    }

    public static SimpleHttpResult httpRequest(SimpleHttpParam httpParam) throws IOException {
        String url = httpParam.getUrl();
        Map<String, String> params = httpParam.getParams();
        String method = httpParam.getMethod();
        String charSet = httpParam.getCharSet();
        Map<String, String> headers = httpParam.getHeaders();

        String response = httpRequest(url, params, method, charSet, headers);
        return new SimpleHttpResult(response);
    }

    public static String mapToQueryString(Map<String, String> parameters, String charSet) throws IOException {
        StringBuilder result = new StringBuilder();
        for (Map.Entry<String, String> entry : parameters.entrySet()) {
            if (result.length() > 0) {
                result.append("&");
            }
            result.append(urlEncode(entry.getKey(), charSet));
            result.append("=");
            result.append(urlEncode(entry.getValue(), charSet));
        }
        return result.toString();
    }

    public static String urlEncode(String url, String charSet) throws IOException {
        return java.net.URLEncoder.encode(url, charSet);
    }

    public static void setSSLSocketFactory(HttpURLConnection urlConn, boolean sslVerify, boolean hostnameVerify, TrustKeyStore trustKeyStore, ClientKeyStore clientKeyStore) throws Exception {
        if (sslVerify) {
            TrustManagerFactory tmf = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
            tmf.init((KeyStore) trustKeyStore.getKeyStore());
            TrustManager[] trustManagers = tmf.getTrustManagers();
            for (TrustManager trustManager : trustManagers) {
                if (trustManager instanceof X509TrustManager) {
                    X509TrustManager x509TrustManager = (X509TrustManager) trustManager;
                    x509TrustManager.checkClientTrusted(new X509Certificate[]{}, "SSL");
                    x509TrustManager.checkServerTrusted(new X509Certificate[]{}, "SSL");
                }
            }
            urlConn.setSSLSocketFactory(new SSLSocketFactory(trustKeyStore, clientKeyStore));
        }
    }

    public static List<String> makeStringList(Object value) {
        if (value instanceof String) {
            List<String> list = new ArrayList<>();
            list.add((String) value);
            return list;
        } else if (value instanceof List) {
            return (List<String>) value;
        } else {
            return new ArrayList<>();
        }
    }

    private static class TrustAnyTrustManager implements X509TrustManager {
        public void checkClientTrusted(X509Certificate[] chain, String authType) {
        }

        public void checkServerTrusted(X509Certificate[] chain, String authType) {
        }

        public X509Certificate[] getAcceptedIssuers() {
            return new X509Certificate[]{};
        }
    }

    private static class TrustAnyHostnameVerifier implements HostnameVerifier {
        public boolean verify(String hostname, SSLSession session) {
            return true;
        }
    }
}

