import org.apache.kafka.clients.admin.AdminClient;
import org.apache.kafka.clients.admin.AdminClientConfig;
import org.apache.kafka.clients.admin.NewTopic;
import org.apache.kafka.common.config.TopicConfig;
import java.util.Collections;
import java.util.Properties;
import java.util.concurrent.ExecutionException;

public boolean createTopic(KafkaClientInfo kafkaClientInfo, NewTopicInfo newTopicInfo) {
    Properties props = new Properties();
    props.put(AdminClientConfig.BOOTSTRAP_SERVERS_CONFIG, kafkaClientInfo.getBootstrapServers());
    AdminClient adminClient = AdminClient.create(props);
    NewTopic newTopic = new NewTopic(newTopicInfo.getName(), newTopicInfo.getPartitions(), newTopicInfo.getReplicationFactor());
    newTopic.configs(Collections.singletonMap(TopicConfig.RETENTION_MS_CONFIG, newTopicInfo.getRetentionTime()));
    try {
        adminClient.createTopics(Collections.singletonList(newTopic)).all().get();
        return true;
    } catch (InterruptedException | ExecutionException e) {
        e.printStackTrace();
        return false;
    } finally {
        adminClient.close();
    }
}

