import java.io.BufferedReader;
import java.io.DataOutputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Map;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public final class HttpURLConnectionUtils {
    private static final int TIMEOUT_IN_MILLIONS = 5000;
    private static final ExecutorService executor = Executors.newCachedThreadPool();

    public interface Callback {
        void onResponse(String result, long response);
        void onFail(Throwable error);
    }

    public static void doGetAsync(String urlStr, Callback callback) {
        executor.submit(() -> request("GET", urlStr, null, null, callback));
    }

    public static void doPostAsync(String urlStr, String params, Callback callback) {
        executor.submit(() -> request("POST", urlStr, null, params, callback));
    }

    private static void request(String method, String urlStr, Map<String, String> headers, String params, Callback callback) {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(urlStr);
            connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod(method);
            connection.setConnectTimeout(TIMEOUT_IN_MILLIONS);
            connection.setReadTimeout(TIMEOUT_IN_MILLIONS);

            if (headers != null) {
                for (Map.Entry<String, String> entry : headers.entrySet()) {
                    connection.setRequestProperty(entry.getKey(), entry.getValue());
                }
            }

            if ("POST".equals(method)) {
                connection.setDoOutput(true);
                try (DataOutputStream wr = new DataOutputStream(connection.getOutputStream())) {
                    wr.writeBytes(params);
                    wr.flush();
                }
            }

            int responseCode = connection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_OK) {
                StringBuilder response = new StringBuilder();
                try (BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()))) {
                    String inputLine;
                    while ((inputLine = in.readLine()) != null) {
                        response.append(inputLine);
                    }
                }
                callback.onResponse(response.toString(), responseCode);
            } else {
                callback.onFail(new IOException("Request failed with HTTP error code: " + responseCode));
            }
        } catch (IOException e) {
            callback.onFail(e);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }
}
