    private static final String CONTENT_ENCODING_HEADER = "Content-Encoding";
    private static final String GZIP_CONTENT_TYPE_HEADER = "gzip";

    public byte[] performRequest(String endpointUrl, ProxyServerInteractor interactor, Map<String, Object> params, SSLSocketFactory socketFactory) throws ServiceUnavailableException, IOException {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(endpointUrl);
            connection = (HttpURLConnection) url.openConnection(interactor.getProxy());
            if (socketFactory != null) {
                connection.setSSLSocketFactory(socketFactory);
            }
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            if (shouldGzipRequestPayload) {
                connection.setRequestProperty(CONTENT_ENCODING_HEADER, GZIP_CONTENT_TYPE_HEADER);
            }
            OutputStream outputStream = connection.getOutputStream();
            getBufferedOutputStream(outputStream).write(params.toString().getBytes());
            outputStream.flush();
            outputStream.close();

            int responseCode = connection.getResponseCode();
            if (responseCode >= MIN_UNAVAILABLE_HTTP_RESPONSE_CODE && responseCode <= MAX_UNAVAILABLE_HTTP_RESPONSE_CODE) {
                throw new ServiceUnavailableException("Service unavailable with response code: " + responseCode);
            }

            InputStream inputStream = connection.getInputStream();
            return slurp(inputStream);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private OutputStream getBufferedOutputStream(OutputStream out) {
        return new BufferedOutputStream(out);
    }

    private boolean isProxyRequest(String endpointUrl) {
