import android.content.Context;
import android.net.Uri;
import android.util.Log;

import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpStatusChecker {
    private Context context;
    private String url;

    public HttpStatusChecker(Context context, String url) {
        this.context = context;
        this.url = url;
    }

    public StatusResult checkStatus() {
        HttpURLConnection connection = null;
        try {
            URL urlObject = new URL(url);
            connection = (HttpURLConnection) urlObject.openConnection();
            connection.setRequestMethod("HEAD");
            connection.connect();

            int responseCode = connection.getResponseCode();
            String message = getResponseMessage(responseCode);
            String redirectionUrl = null;

            if (responseCode == HttpURLConnection.HTTP_MOVED_TEMP
                    || responseCode == HttpURLConnection.HTTP_MOVED_PERM
                    || responseCode == HttpURLConnection.HTTP_SEE_OTHER) {
                redirectionUrl = connection.getHeaderField("Location");
            }

            return new StatusResult(message, redirectionUrl);
        } catch (IOException e) {
            Log.e("HttpStatusChecker", "Error checking HTTP status", e);
            return new StatusResult("Error checking HTTP status", null);
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private String getResponseMessage(int responseCode) {
        switch (responseCode) {
            case HttpURLConnection.HTTP_OK:
                return "HTTP OK";
            case HttpURLConnection.HTTP_MOVED_TEMP:
                return "HTTP Moved Temporarily";
            case HttpURLConnection.HTTP_MOVED_PERM:
                return "HTTP Moved Permanently";
            case HttpURLConnection.HTTP_SEE_OTHER:
                return "HTTP See Other";
            case HttpURLConnection.HTTP_BAD_REQUEST:
                return "HTTP Bad Request";
            case HttpURLConnection.HTTP_UNAUTHORIZED:
                return "HTTP Unauthorized";
            case HttpURLConnection.HTTP_NOT_FOUND:
                return "HTTP Not Found";
            case HttpURLConnection.HTTP_INTERNAL_ERROR:
                return "HTTP Internal Server Error";
            case HttpURLConnection.HTTP_NOT_IMPLEMENTED:
                return "HTTP Not Implemented";
            default:
                return "HTTP Unknown Status";
        }
    }

    public static class StatusResult {
        private String message;
        private String redirectionUrl;

        public StatusResult(String message, String redirectionUrl) {
            this.message = message;
            this.redirectionUrl = redirectionUrl;
        }

        public String getMessage() {
            return message;
        }

        public String getRedirectionUrl() {
            return redirectionUrl;
        }
    }
}
