    public static String sendRequest(HttpRequestInfo httpRequestInfo, String cookieValue) {
        HttpURLConnection connection = createConnection(httpRequestInfo);
        configureRequest(connection, httpRequestInfo, cookieValue);
        if (httpRequestInfo.hasRequestBody()) {
            sendRequestBody(connection, httpRequestInfo);
        }
        return processResponse(connection);
    }

    private static HttpURLConnection createConnection(HttpRequestInfo httpRequestInfo) {
        URL url;
        try {
            url = new URL(httpRequestInfo.getUrl());
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            if (config.isProxyEnabled()) {
                connection.setProxy(new Proxy(Proxy.Type.HTTP, new InetSocketAddress(config.getProxyHost(), config.getProxyPort())));
            }
            connection.setRequestMethod(httpRequestInfo.getMethod());
            connection.setConnectTimeout(config.getConnectionTimeout());
            connection.setReadTimeout(config.getReadTimeout());
            return connection;
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }

    private static void configureRequest(HttpURLConnection connection, HttpRequestInfo httpRequestInfo, String cookieValue) {
        connection.setRequestProperty("Cookie", cookieValue);
        for (Map.Entry<String, String> header : httpRequestInfo.getHeaders().entrySet()) {
            connection.setRequestProperty(header.getKey(), header.getValue());
        }
    }

    private static void configureCookie(Map<String, String> headers, String cookieValue) {
        if (cookieValue != null) {
            headers.put("Cookie", cookieValue);
        }
    }

    private static void sendRequestBody(HttpURLConnection connection, HttpRequestInfo httpRequestInfo) throws IOException {
        if (httpRequestInfo.getRequestBody() != null) {
            connection.setDoOutput(true);
            try (OutputStream os = connection.getOutputStream()) {
                byte[] input = httpRequestInfo.getRequestBody().getBytes("utf-8");
                os.write(input, 0, input.length);
            }
        }
