            byte[] buffer = new byte[1024];
            int length;
            while ((length = is.read(buffer)) != -1) {
                baos.write(buffer, 0, length);
            }
            return new String(baos.toByteArray(), "utf-8");
        } finally {
            connection.disconnect();
        }
    }

    private static void closeResources(HttpURLConnection connection, InputStream inputStream, ByteArrayOutputStream baos) {
        if (inputStream != null) {
            try {
                inputStream.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (baos != null) {
            try {
