import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardCopyOption;
import java.util.Optional;

public class HttpDownloader {
    private static final Logger logger = LoggerFactory.getLogger(HttpDownloader.class);
    private static final int MAXIMUM_REDIRECTS = 8;

    public void download(String targetUrlString, Path path, String userAgent) throws IOException {
        Optional<String> nextUrl = Optional.of(targetUrlString);
        int redirectCount = 0;
        while (nextUrl.isPresent() && redirectCount < MAXIMUM_REDIRECTS) {
            nextUrl = getDownloadedOrNextUrl(nextUrl.get(), path, userAgent);
            redirectCount++;
        }
        if (redirectCount >= MAXIMUM_REDIRECTS) {
            logger.error("Exceeded maximum redirect limit of {}", MAXIMUM_REDIRECTS);
            throw new IOException("Exceeded maximum redirect limit");
        }
    }

    private Optional<String> getDownloadedOrNextUrl(String urlString, Path path, String userAgent) throws IOException {
        URL url = new URL(urlString);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setRequestProperty("User-Agent", userAgent);
        connection.setInstanceFollowRedirects(false);
        connection.connect();

        int responseCode = connection.getResponseCode();
        if (responseCode == HttpURLConnection.HTTP_OK) {
            try (InputStream inputStream = connection.getInputStream()) {
                Files.copy(inputStream, path, StandardCopyOption.REPLACE_EXISTING);
                logger.info("Downloaded file to {}", path);
            }
            return Optional.empty();
        } else if (responseCode == HttpURLConnection.HTTP_MOVED_PERM || responseCode == HttpURLConnection.HTTP_MOVED_TEMP || responseCode == HttpURLConnection.HTTP_SEE_OTHER) {
            String redirectUrl = connection.getHeaderField("Location");
            logger.info("Redirecting to {}", redirectUrl);
            return Optional.of(redirectUrl);
        } else {
            logger.error("Failed to download file. Server responded with status code: {}", responseCode);
            throw new IOException("Failed to download file. Server responded with status code: " + responseCode);
        }
    }
}

