        this.process = null;
        this.stdout = null;
        this.error = null;
        this.exitval = -1;
    }

    public void start(boolean waitForExit) {
        try {
            process = rt.exec(command);
            stdout = new BufferedReader(new InputStreamReader(process.getInputStream()));
            if (waitForExit) {
                waitForExit();
            }
        } catch (IOException e) {
            error = e.getMessage();
            Log.e(tag, "Error starting process: " + error);
        }
    }

    public void waitForExit() {
        try {
            while (!checkForExit()) {
                if (stdoutAvailable()) {
                    readStdoutBlocking();
                }
            }
            exitval = process.exitValue();
        } catch (Exception e) {
            error = e.getMessage();
            Log.e(tag, "Error waiting for process exit: " + error);
        }
    }

    public void finish() {
        if (process != null) {
            process.destroy();
        }
        try {
            if (stdout != null) {
                stdout.close();
            }
        } catch (IOException e) {
            Log.e(tag, "Error closing stdout: " + e.getMessage());
        }
    }

    public boolean checkForExit() {
        try {
            exitval = process.exitValue();
            return true;
        } catch (IllegalThreadStateException e) {
            return false;
        }
    }

    public boolean stdoutAvailable() {
        try {
            return stdout.ready();
        } catch (IOException e) {
            Log.e(tag, "Error checking stdout availability: " + e.getMessage());
            return false;
        }
    }

    public String readStdoutBlocking() {
        try {
            return stdout.readLine();
        } catch (IOException e) {
            Log.e(tag, "Error reading stdout: " + e.getMessage());
            return null;
        }
    }

    public String readStdout() {
        if (stdoutAvailable()) {
            return readStdoutBlocking();
        }
        return null;
    }
}
