import redis.clients.jedis.Connection;
import redis.clients.jedis.JedisConnectionException;
import redis.clients.jedis.JedisDataException;
import redis.clients.jedis.JedisException;
import redis.clients.jedis.Protocol.Command;
import redis.clients.jedis.Response;
import redis.clients.jedis.commands.CommandObjects;
import redis.clients.jedis.graph.GraphCommandObjects;
import redis.clients.jedis.util.RedisProtocol;

import java.util.ArrayList;
import java.util.LinkedList;
import java.util.List;
import java.util.Queue;

public class ReliableTransaction {

    private static final String QUEUED_STR = "QUEUED";
    private final Queue<Response<?>> pipelinedResponses = new LinkedList<>();
    protected final Connection connection;
    private final boolean closeConnection;
    private boolean broken = false;
    private boolean inWatch = false;
    private boolean inMulti = false;
    private final CommandObjects commandObjects;

    public ReliableTransaction(Connection connection, boolean doMulti, boolean closeConnection, CommandObjects commandObjects) {
        this.connection = connection;
        this.closeConnection = closeConnection;
        this.commandObjects = commandObjects;
        GraphCommandObjects graphCommandObjects = new GraphCommandObjects(this.connection);
        graphCommandObjects.setBaseCommandArgumentsCreator(protocolCommand -> commandObjects.commandArguments(protocolCommand));
        if (doMulti) multi();
    }

    public void multi() {
        connection.sendCommand(Command.MULTI);
        String status = connection.getStatusCodeReply();
        if (!"OK".equals(status)) {
            throw new JedisException("MULTI command failed. Received response: " + status);
        }
        inMulti = true;
    }

    public String watch(final String... keys) {
        String status = connection.executeCommand(commandObjects.watch(keys));
        inWatch = true;
        return status;
    }

    public String unwatch() {
        connection.sendCommand(Command.UNWATCH);
        String status = connection.getStatusCodeReply();
        inWatch = false;
        return status;
    }

    protected <T> Response<T> appendCommand(CommandObject<T> commandObject) {
        connection.sendCommand(commandObject.getArguments());
        String status = connection.getStatusCodeReply();
        if (!QUEUED_STR.equals(status)) {
            throw new JedisException(status);
        }
        Response<T> response = new Response<>(commandObject.getBuilder());
        pipelinedResponses.add(response);
        return response;
    }

    public void close() {
        try {
            clear();
        } finally {
            if (closeConnection) {
                connection.close();
            }
        }
    }

    public void clear() {
        if (broken) return;
        if (inMulti) {
            discard();
        } else if (inWatch) {
            unwatch();
        }
    }

    public List<Object> exec() {
        if (!inMulti) throw new IllegalStateException("EXEC without MULTI");

        try {
            connection.sendCommand(Command.EXEC);
            List<Object> unformatted = connection.getObjectMultiBulkReply();
            if (unformatted == null) {
                pipelinedResponses.clear();
                return null;
            }

            List<Object> formatted = new ArrayList<>(unformatted.size());
            for (Object o : unformatted) {
                try {
                    Response<?> response = pipelinedResponses.poll();
                    response.set(o);
                    formatted.add(response.get());
                } catch (JedisDataException e) {
                    formatted.add(e);
                }
            }
            return formatted;
        } catch (JedisConnectionException jce) {
            broken = true;
            throw jce;
        } finally {
            inMulti = false;
            inWatch = false;
            pipelinedResponses.clear();
        }
    }

    public String discard() {
        if (!inMulti) throw new IllegalStateException("DISCARD without MULTI");

        try {
            connection.sendCommand(Command.DISCARD);
            String status = connection.getStatusCodeReply();
            if (!"OK".equals(status)) {
                throw new JedisException("DISCARD command failed. Received response: " + status);
            }
            return status;
        } catch (JedisConnectionException jce) {
            broken = true;
            throw jce;
        } finally {
            inMulti = false;
            inWatch = false;
            pipelinedResponses.clear();
        }
    }
}

