            if (key.isReadable()) {
                read();
            }
            if (key.isWritable()) {
                write();
            }
        }

        private void read() throws IOException {
            while (chan.read(oblivion) > 0) {
                oblivion.clear();
            }
        }

        private void write() throws IOException {
            while ((toWrite = writeQueue.poll()) != null) {
                chan.write(toWrite);
                if (toWrite.hasRemaining()) {
                    awaitingWriteNotification = true;
                    return;
                }
            }
            awaitingWriteNotification = false;
            key.interestOps(SelectionKey.OP_READ);
        }

        public void doStateChecks(long now) {
            // Perform periodic state checks
        }

        public int calcInterestOps() {
            int ops = SelectionKey.OP_READ;
            if (awaitingWriteNotification || !writeQueue.isEmpty()) {
                ops |= SelectionKey.OP_WRITE;
            }
            return ops;
        }
    }
