                  if (res.succeeded()) {
                      System.out.println("Server started on " + host + ":" + port);
                  } else {
                      System.out.println("Failed to start server: " + res.cause().getMessage());
                  }
              });
    }

    private void handleConnection(NetSocket socket) {
        socket.handler(buffer -> {
            System.out.println("Received data: " + buffer.toString());
            socket.write(buffer);
        }).endHandler(v -> {
            System.out.println("Client disconnected");
        }).exceptionHandler(err -> {
            System.out.println("Error occurred: " + err.getMessage());
        });
    }

    public void stopServer() {
        if (server != null) {
            server.close(res -> {
                if (res.succeeded()) {
                    System.out.println("Server stopped successfully");
                } else {
                    System.out.println("Failed to stop server: " + res.cause().getMessage());
                }
            });
        }
    }

    @Override
    public void start() {
        startServer(vertx, 1234, "localhost");
    }

    @Override
    public void stop() {
        stopServer();
    }

    public static void main(String[] args) {
        Vertx vertx = Vertx.vertx();
        vertx.deployVerticle(new VertxTcpServer());
    }
