        StringBuilder sb = new StringBuilder();

        try
        {
            sb.append("http://");
            sb.append(mIcecastConfiguration.getHost());
            sb.append(":");
            sb.append(mIcecastConfiguration.getPort());
            sb.append("/admin/metadata?mode=updinfo&mount=");
