        catch(UnsupportedEncodingException uee)
        {
            mLog.error("Error encoding metadata information to UTF-8", uee);
            sb = null;
        }

        if(sb != null)
        {
            final String metadataUpdateURL = sb.toString();
            URI uri = URI.create(metadataUpdateURL);

