            var builder = new StringBuilder("");
            String str;
            while ((str = reader.readLine()) != null) {
                builder.append(str).append("\n");
            }
            cleanup(client);
            return builder.toString();
        } catch (Exception e) {
            throw new IllegalStateException(e);
        }
    }

    private void cleanup(Closeable c) {
        if (c != null) {
            try {
                c.close();
            } catch (IOException e) {
