        if (out2 != null) {
            try {
                out2.close();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
        if (bout != null) {
            try {
                bout.close();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
        if (connection != null) {
            try {
                connection.disconnect();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
