import javax.net.ssl.HttpsURLConnection;
import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpDebugIdSender {
    private final String distinctId;
    private final String infoId;
    private final String serverUrl;

    public HttpDebugIdSender(String serverUrl, String distinctId, String infoId) {
        this.serverUrl = serverUrl;
        this.distinctId = distinctId;
        this.infoId = infoId;
    }

    public void send() {
        try {
            sendHttpRequest(serverUrl, true);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void sendHttpRequest(String serverUrl, boolean isRedirects) throws IOException {
        URL url = new URL(serverUrl + "?distinctId=" + distinctId + "&infoId=" + infoId);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        if (connection instanceof HttpsURLConnection) {
            ((HttpsURLConnection) connection).setSSLSocketFactory((HttpsURLConnection.getDefaultSSLSocketFactory()));
        }
        connection.setRequestMethod("POST");
        connection.setDoOutput(true);
        connection.setRequestProperty("Content-Type", "application/json");

        ByteArrayOutputStream out = new ByteArrayOutputStream();
        OutputStream out2 = null;
        BufferedOutputStream bout = null;

        try {
            out.write(("{\"distinctId\":\"" + distinctId + "\",\"infoId\":\"" + infoId + "\"}").getBytes());
            out2 = connection.getOutputStream();
            bout = new BufferedOutputStream(out2);
            bout.write(out.toByteArray());
            bout.flush();

            int responseCode = connection.getResponseCode();
            if (isRedirects && (responseCode == HttpURLConnection.HTTP_MOVED_PERM || responseCode == HttpURLConnection.HTTP_MOVED_TEMP)) {
                String newUrl = connection.getHeaderField("Location");
                sendHttpRequest(newUrl, false);
            } else if (responseCode != HttpURLConnection.HTTP_OK) {
                System.err.println("Failed to send debug info: " + responseCode);
            }
        } finally {
            closeStream(out, out2, bout, connection);
        }
    }

    private void closeStream(ByteArrayOutputStream out, OutputStream out2, BufferedOutputStream bout, HttpURLConnection connection) {
        try {
            if (bout != null) bout.close();
            if (out2 != null) out2.close();
            if (out != null) out.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        if (connection != null) {
            connection.disconnect();
        }
    }
}

