            next.accept(new Tile(TileCoord.hilbertDecode((int) (entry.tileId() + i)), data));
          }
        }
      } catch (IOException e) {
        throw new IllegalStateException("Failed to iterate through pmtiles archive ", e);
      }
    });
  }
}

