import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslHandler;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import javax.net.ssl.SSLEngine;
import java.util.concurrent.TimeUnit;

public class ProxyClientContainer {
    private static final Logger logger = LoggerFactory.getLogger(ProxyClientContainer.class);
    private final NioEventLoopGroup workerGroup;
    private final Bootstrap bootstrap;
    private final Bootstrap realServerBootstrap;
    private final Config config;
    private final SslContext sslContext;
    private long sleepTimeMill = 1000;

    public ProxyClientContainer(Config config, SslContext sslContext) {
        this.config = config;
        this.sslContext = sslContext;
        this.workerGroup = new NioEventLoopGroup();
        this.bootstrap = new Bootstrap();
        this.realServerBootstrap = new Bootstrap();
        configureBootstrap();
    }

    private void configureBootstrap() {
        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (sslContext != null) {
                            pipeline.addLast(createSslHandler(sslContext));
                        }
                        // Add other handlers as needed
                    }
                });
    }

    public void start() {
        connectProxyServer();
    }

    private SslHandler createSslHandler(SslContext sslContext) {
        SSLEngine sslEngine = sslContext.newEngine(workerGroup.next().alloc());
        sslEngine.setUseClientMode(true);
        return new SslHandler(sslEngine);
    }

    private void connectProxyServer() {
        bootstrap.connect(config.getProxyHost(), config.getProxyPort()).addListener((ChannelFutureListener) future -> {
            if (future.isSuccess()) {
                logger.info("Connected to proxy server");
                sleepTimeMill = 1000; // Reset backoff time
            } else {
                logger.error("Failed to connect to proxy server, retrying...", future.cause());
                reconnectWait();
                connectProxyServer();
            }
        });
    }

    public void stop() {
        workerGroup.shutdownGracefully();
    }

    public void channelInactive(ChannelHandlerContext ctx) {
        logger.warn("Channel inactive, attempting to reconnect...");
        reconnectWait();
        connectProxyServer();
    }

    private void reconnectWait() {
        try {
            logger.info("Waiting for {} ms before reconnecting", sleepTimeMill);
            TimeUnit.MILLISECONDS.sleep(sleepTimeMill);
            sleepTimeMill = Math.min(sleepTimeMill * 2, 30000); // Cap backoff at 30 seconds
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }
}

