import io.netty.handler.codec.LengthFieldPrepender;
import io.netty.handler.codec.serialization.ClassResolvers;
import io.netty.handler.codec.serialization.ObjectDecoder;
import io.netty.handler.codec.serialization.ObjectEncoder;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import javax.annotation.PreDestroy;
import java.util.concurrent.TimeUnit;

@Component
public class NettyClient {

    private static final int RECONNECT_SECONDS = 5;
    private static final Logger logger = LoggerFactory.getLogger(NettyClient.class);

    @Value("${server.host}")
    private String serverHost;

    @Value("${server.port}")
    private int serverPort;

    private final EventLoopGroup eventGroup = new NioEventLoopGroup();
    private volatile Channel channel;

    public void start() {
        Bootstrap bootstrap = new Bootstrap();
        bootstrap.group(eventGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) throws Exception {
                        ch.pipeline().addLast(
                                new LengthFieldBasedFrameDecoder(65536, 0, 4, 0, 4),
                                new LengthFieldPrepender(4),
                                new ObjectEncoder(),
                                new ObjectDecoder(ClassResolvers.cacheDisabled(null))
                        );
                    }
                });

        connect(bootstrap);
    }

    private void connect(Bootstrap bootstrap) {
        ChannelFuture future = bootstrap.connect(serverHost, serverPort);
        future.addListener((ChannelFuture f) -> {
            if (f.isSuccess()) {
                logger.info("Connected to server {}:{}", serverHost, serverPort);
                channel = f.channel();
            } else {
                logger.error("Failed to connect to server {}:{}", serverHost, serverPort);
                reconnect();
            }
        });
    }

    private void reconnect() {
        eventGroup.schedule(() -> {
            logger.info("Attempting to reconnect...");
            start();
        }, RECONNECT_SECONDS, TimeUnit.SECONDS);
    }

    @PreDestroy
    public void shutdown() {
        if (channel != null) {
            channel.close();
        }
        eventGroup.shutdownGracefully();
        logger.info("Client shutdown complete.");
    }

    public void send(Invocation invocation) {
        if (channel != null && channel.isActive()) {
            channel.writeAndFlush(invocation);
        } else {
            logger.warn("Cannot send message, channel is not active.");
        }
    }
}

