import io.netty.bootstrap.Bootstrap;
import io.netty.channel.Channel;
import io.netty.channel.ChannelFuture;
import io.netty.channel.ChannelInitializer;
import io.netty.channel.ChannelOption;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.codec.string.StringEncoder;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;

import java.nio.charset.StandardCharsets;
import java.util.concurrent.ConcurrentHashMap;
import java.util.Map;

public class NettyTcpClientManager {
    private final Map<String, Channel> channelMap = new ConcurrentHashMap<>();
    private final Map<String, NioEventLoopGroup> groupMap = new ConcurrentHashMap<>();

    public Channel initSocketClientAndSendMessage(String host, String message) throws InterruptedException {
        Channel channel = channelMap.get(host);
        if (channel == null || !channel.isActive()) {
            NioEventLoopGroup group = new NioEventLoopGroup();
            Bootstrap bootstrap = new Bootstrap();
            bootstrap.group(group)
                     .channel(NioSocketChannel.class)
                     .handler(new ChannelInitializer<SocketChannel>() {
                         @Override
                         protected void initChannel(SocketChannel ch) {
                             ch.pipeline().addLast(new LoggingHandler(LogLevel.INFO), new StringEncoder(StandardCharsets.UTF_8));
                         }
                     })
                     .option(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture future = bootstrap.connect(host, 8080).sync();
            channel = future.channel();
            channelMap.put(host, channel);
            groupMap.put(host, group);
        }

        if (channel != null && channel.isActive()) {
            channel.writeAndFlush(message);
        }
        return channel;
    }

    public void closeAllConnections() {
        for (Map.Entry<String, Channel> entry : channelMap.entrySet()) {
            Channel channel = entry.getValue();
            if (channel != null && channel.isActive()) {
                channel.close();
            }
        }
        for (Map.Entry<String, NioEventLoopGroup> entry : groupMap.entrySet()) {
            NioEventLoopGroup group = entry.getValue();
            if (group != null) {
                group.shutdownGracefully();
            }
        }
        channelMap.clear();
        groupMap.clear();
    }
}

