import io.netty.handler.codec.LengthFieldPrepender;
import io.netty.handler.codec.serialization.ClassResolvers;
import io.netty.handler.codec.serialization.ObjectDecoder;
import io.netty.handler.codec.serialization.ObjectEncoder;

import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;

public class NettyRemotingClient {

    private final Bootstrap bootstrap;
    private final EventLoopGroup eventLoopGroupWorker;

    public NettyRemotingClient() {
        this.eventLoopGroupWorker = new NioEventLoopGroup();
        this.bootstrap = new Bootstrap();
        this.bootstrap.group(eventLoopGroupWorker)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.TCP_NODELAY, true)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast(new LengthFieldBasedFrameDecoder(65535, 0, 4, 0, 4));
                        pipeline.addLast(new LengthFieldPrepender(4));
                        pipeline.addLast(new ObjectEncoder());
                        pipeline.addLast(new ObjectDecoder(ClassResolvers.cacheDisabled(null)));
                        pipeline.addLast(new NettyClientHandler());
                    }
                });
    }

    public ChannelFuture createChannel(String addr) throws RemotingConnectException {
        String[] hostPort = addr.split(":");
        if (hostPort.length != 2) {
            throw new RemotingConnectException("Invalid address format");
        }
        String host = hostPort[0];
        int port = Integer.parseInt(hostPort[1]);
        ChannelFuture future = bootstrap.connect(host, port);
        future.awaitUninterruptibly();
        if (!future.isSuccess()) {
            throw new RemotingConnectException("Connection failed");
        }
        return future;
    }

    public CompletableFuture<RemotingCommand> invokeSync(Channel channel, RemotingCommand request, long timeoutMillis) {
        CompletableFuture<RemotingCommand> responseFuture = new CompletableFuture<>();
        channel.writeAndFlush(request).addListener((ChannelFutureListener) future -> {
            if (future.isSuccess()) {
                channel.pipeline().get(NettyClientHandler.class).setResponseFuture(responseFuture);
            } else {
                responseFuture.completeExceptionally(future.cause());
            }
        });
        return responseFuture.orTimeout(timeoutMillis, TimeUnit.MILLISECONDS);
    }

    public void shutdown() {
        eventLoopGroupWorker.shutdownGracefully();
    }

    private class NettyClientHandler extends SimpleChannelInboundHandler<RemotingCommand> {

        private CompletableFuture<RemotingCommand> responseFuture;

        public void setResponseFuture(CompletableFuture<RemotingCommand> responseFuture) {
            this.responseFuture = responseFuture;
        }

        @Override
