import java.util.concurrent.Executors;

public class NettyServer {
    private Thread serverThread;
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private ExecutorService requestProcessingPool;

    public void start(XxlRpcProviderFactory xxlRpcProviderFactory) {
        serverThread = new Thread(() -> {
            bossGroup = new NioEventLoopGroup();
            workerGroup = new NioEventLoopGroup();
            requestProcessingPool = Executors.newFixedThreadPool(xxlRpcProviderFactory.getThreadPoolSize());

            try {
                ServerBootstrap bootstrap = new ServerBootstrap();
                bootstrap.group(bossGroup, workerGroup)
                         .channel(NioServerSocketChannel.class)
                         .childHandler(new ChannelInitializer<SocketChannel>() {
                             @Override
                             protected void initChannel(SocketChannel ch) {
                                 ch.pipeline().addLast(new IdleStateHandler(0, 0, xxlRpcProviderFactory.getIdleTimeout()));
                                 ch.pipeline().addLast(new RpcRequestHandler(xxlRpcProviderFactory, requestProcessingPool));
                             }
                         })
                         .option(ChannelOption.SO_BACKLOG, 128)
                         .childOption(ChannelOption.SO_KEEPALIVE, true);

                ChannelFuture future = bootstrap.bind(xxlRpcProviderFactory.getPort()).sync();
                future.channel().closeFuture().sync();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            } finally {
                shutdown();
            }
        });
