            serverThread.interrupt();
        }
        shutdown();
    }

    private void shutdown() {
        if (bossGroup != null) {
            bossGroup.shutdownGracefully();
        }
        if (workerGroup != null) {
            workerGroup.shutdownGracefully();
        }
        if (requestProcessingPool != null) {
            requestProcessingPool.shutdown();
        }
    }
